/* eslint-disable react/jsx-props-no-spreading */
import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { HeaderBackButton } from '@react-navigation/elements';
import { HelperStyle } from 'react-native-minerva';
import labels from './constants';

const propTypes = {
    accessibilityLabel: PropTypes.string,
    testID: PropTypes.string,
    tintColor: PropTypes.oneOfType([PropTypes.string, PropTypes.bool]),
    truncatedLabel: PropTypes.string,
};

const defaultProps = {
    accessibilityLabel: labels.BUTTON_VOLVER,
    testID: labels.BUTTON_VOLVER,
    tintColor: false,
    truncatedLabel: '',
};

const HeaderBackButtonBCH = (props) => {
    const { tintColor, accessibilityLabel, testID } = props;
    return (
        <HeaderBackButton
            accessible
            accessibilityLabel={accessibilityLabel}
            testID={testID}
            {...props}
            tintColor={tintColor || HelperStyle.gray.color}
        />
    );
};

HeaderBackButtonBCH.propTypes = propTypes;
HeaderBackButtonBCH.defaultProps = defaultProps;

export default memo(HeaderBackButtonBCH);
