/* eslint-disable react/jsx-props-no-spreading */
import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { HelperStyle, LinearGradient } from 'react-native-minerva';

const propTypes = {
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array, PropTypes.string]),
};

const defaultProps = {
    style: undefined,
};
/*
 * Color de fondo del header
 */
const HeaderBackground = (props) => {
    const { style } = props;
    return <LinearGradient {...props} style={[HelperStyle.w_100, HelperStyle.h_100, HelperStyle.p_a, style]} />;
};

HeaderBackground.propTypes = propTypes;
HeaderBackground.defaultProps = defaultProps;

export default memo(HeaderBackground);
