/* eslint-disable react/jsx-props-no-spreading */
import React from 'react';
import PropTypes from 'prop-types';
import { HelperStyle, H4, isIphone, Platform } from 'react-native-minerva';

const propTypes = {
    children: PropTypes.node.isRequired,
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};

const defaultProps = {
    style: {},
};

const HeaderGradientTitle = (props) => {
    const { children, style } = props;
    const size = children.length;
    const maxLengthTitle = 20;
    const fontSize = size > maxLengthTitle && isIphone.Small ? HelperStyle.fs_2 : HelperStyle.fs_3;
    const textCenter = Platform.ios && HelperStyle.ta_c;
    return (
        <H4
            headerTitle
            accessibilityLabel={children}
            testID={children}
            {...props}
            style={[HelperStyle.white, fontSize, textCenter, style]}
        >
            {children}
        </H4>
    );
};

HeaderGradientTitle.propTypes = propTypes;
HeaderGradientTitle.defaultProps = defaultProps;

export default HeaderGradientTitle;
