import React from 'react';
import Menu from '../containers/Menu';

const MenuDrawerNavigatorOptions = ({ params }) => ({
    drawerContent: ({ state, navigation, descriptors, progress }) => (
        <Menu route={{ params }} state={state} navigation={navigation} descriptors={descriptors} progress={progress} />
    ),
    drawerPosition: 'right',
    drawerType: 'front',
    drawerStyle: { width: '100%' },
});

export default MenuDrawerNavigatorOptions;
