import React from 'react';
import PropTypes from 'prop-types';
import {
    HelperStyle,
    Grid,
    Col,
    Skeleton,
} from 'react-native-minerva';

/**
 * Renderiza Skeleton Bullets del Card de Producto.
 * 
 * @component
 */
const SkeletonBullets = ({ placeholderStyle, style }) => (
    <Grid>
        <Col size={4} />
        <Col size={2}>
            <Skeleton numberOfItems={1} style={[HelperStyle.mb_5, style]} placeholderStyle={placeholderStyle} />
        </Col>
        <Col size={4} />
    </Grid>
);

SkeletonBullets.propTypes = {
    /**
     * Estilos para contenedor del Skeleton.
     */
    placeholderStyle: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
    /**
     * Estilos para skeleton.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
SkeletonBullets.defaultProps = {
    placeholderStyle: null,
    style: null,
};

export default SkeletonBullets;
