import React from 'react';
import PropTypes from 'prop-types';
import {
    HelperStyle,
    Card,
    CardItem,
    Grid,
    Col,
    Skeleton,
} from 'react-native-minerva';

/**
 * Renderiza Skeleton card.
 * 
 * @component
 */
const SkeletonCardProducto = ({ buttonPay, style }) => {
    const renderButtonPay = () => {
        if (buttonPay) {
            return (
                <Col size={2} style={HelperStyle.ai_fe}>
                    <Skeleton numberOfItems={1} />
                </Col>
            );
        }
        return (
            <Col size={2} />
        );
    };

    return (
        <Card style={[HelperStyle.mb_2, style]}>
            <CardItem body style={HelperStyle.pt_4}>
                <Grid>
                    <Col size={6}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                    <Col size={2} />
                    {renderButtonPay()}
                </Grid>
                <Grid style={HelperStyle.mt_2}>
                    <Col size={4}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                    <Col size={6} />
                </Grid>
                <Grid style={HelperStyle.mt_4}>
                    <Col size={2} style={[HelperStyle.jc_c]}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                    <Col size={2} />
                    <Col size={3}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                </Grid>
                <Grid style={HelperStyle.mt_2}>
                    <Col size={2} style={[HelperStyle.jc_c]}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                    <Col size={3} />
                    <Col size={2}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                </Grid>
            </CardItem>
        </Card>
    );
};

SkeletonCardProducto.propTypes = {
    /**
     * Boleano para mostrar skeleton bot√≥n pagar.
     */
    buttonPay: PropTypes.string,
    /**
     * Estilos para contenedor del Skeleton.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
SkeletonCardProducto.defaultProps = {
    buttonPay: false,
    style: null,
};

export default SkeletonCardProducto;
