import React from 'react';
import PropTypes from 'prop-types';
import {
    HelperStyle,
    ListItem,
    Grid,
    Col,
    Skeleton,
    Card,
    CardItem,
} from 'react-native-minerva';

const propTypes = {
    numberOfItems: PropTypes.number,
};

const defaultProps = {
    numberOfItems: 1,
};

const SkeletonMultiLinea = ({ numberOfItems }) => {
    const renderItems = (item, index) => {
        const lastItem = Array(...Array(numberOfItems)).length - 1;
        return (
            <ListItem noBorder={index === lastItem}>
                <Grid>
                    <Col size={4}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                    <Col size={3} />
                    <Col size={3} style={HelperStyle.ai_fe}>
                        <Skeleton numberOfItems={1} />
                    </Col>
                </Grid>
            </ListItem>
        );
    };

    return (
        <Card noShadow style={[HelperStyle.bg_gray_background]}>
            <CardItem body small style={HelperStyle.pb_0}>
                {Array(...Array(numberOfItems)).map((item, index) => renderItems(item, index))}
            </CardItem>
        </Card>
    );
};

SkeletonMultiLinea.propTypes = propTypes;
SkeletonMultiLinea.defaultProps = defaultProps;

export default SkeletonMultiLinea;
