import React from 'react';
import PropTypes from 'prop-types';
import {
    HelperStyle,
    View,
    Grid,
    Col,
    Skeleton,
    Card,
    CardItem,
} from 'react-native-minerva';

const propTypes = {
    numberOfItems: PropTypes.number,
};

const defaultProps = {
    numberOfItems: 1,
};

const SkeletonMultiLinea = ({ numberOfItems }) => {
    const renderItems = (item, index) => (
        <CardItem body style={index === 0 && HelperStyle.pt_4}>
            <Grid>
                <Col size={5}>
                    <Skeleton numberOfItems={1} />
                </Col>
                <Col size={3} />
                <Col size={2} style={HelperStyle.ai_fe}>
                    <Skeleton numberOfItems={1} />
                </Col>
            </Grid>
        </CardItem>
    );

    return (
        <View>
            <Skeleton numberOfItems={1} width={40} style={HelperStyle.mb_4} />
            <Card>
                {Array(...Array(numberOfItems)).map((item, index) => renderItems(item, index))}
            </Card>
        </View>
    );
};

SkeletonMultiLinea.propTypes = propTypes;
SkeletonMultiLinea.defaultProps = defaultProps;

export default SkeletonMultiLinea;
