import React, { memo } from 'react';
import {
    View,
    HelperStyle,
    Skeleton,
    ListItem,
    Grid,
    Col,
} from 'react-native-minerva';

const SkeletonRubrosNoDestacados = () => {
    const renderItems = () => {
        const textCol = Math.floor(Math.random() * 9) + 1;
        const emptyCol = 9 - textCol;
        return (
            <ListItem
                avatar
                style={[HelperStyle.mb_3, HelperStyle.pt_4, HelperStyle.pb_4, HelperStyle.bbw_0]}
            >
                <Grid style={HelperStyle.jc_c}>
                    <Col size={1}>
                        <Skeleton numberOfItems={1} type="media" size={28} contenContainerStyle={HelperStyle.mb_0} />
                    </Col>
                    <Col size={textCol} style={HelperStyle.pl_2}>
                        <Skeleton numberOfItems={1} contenContainerStyle={HelperStyle.mb_0} />
                    </Col>
                    <Col size={emptyCol} />
                </Grid>
            </ListItem>
        );
    };
    return (
        <View style={[HelperStyle.flx_i]}>
            {renderItems()}
            {renderItems()}
            {renderItems()}
            {renderItems()}
            {renderItems()}
            {renderItems()}
        </View>
    );
};

export default memo(SkeletonRubrosNoDestacados);
