/**
 * Separar nombre completo en un arreglo de name y lastName
 * @param   {string} fullName  Nombre completo
 * @return  {Array}  Retorna primera posición name segunda posición lastName
 */
const splitFullName = (fullName) => fullName?.split(' ') || '';

/**
 * Buscar los emoji en un string
 * @param   {string} text
 * @return  {Array}  Retorna arreglo de emojis
 */
const hasEmoji = (text) => text.match(/\p{Emoji}/gu);

/**
 * Obtener el primer caracter de un string.
 * @param {String} text
 * @returns {String}
 */
const getFirstChar = (text = '') => (text ? text.toString().trim().split(' ')[0].split('')[0] : '');

/**
 * Obtener la primera posición de un arreglo de emojis
 * @param {Array}
 * @returns {String} retorna el primer emoji
 */
const getFirstEmoji = (array) => array[0];

/**
 * Obtener las iniciales del nombre y apellido
 * @param {String} name
 * @param {String} lastName
 * @returns {String} NA
 */
const getInitials = (name = '', lastName = '') => {
    const nameInitial = hasEmoji(name) ? getFirstEmoji(hasEmoji(name)) : getFirstChar(name).toUpperCase();
    const surnameInitial = hasEmoji(lastName)
        ? getFirstEmoji(hasEmoji(lastName))
        : getFirstChar(lastName).toUpperCase();
    return `${nameInitial}${surnameInitial}`;
};

export default {
    getInitials,
    splitFullName,
};
