import moment from 'moment';
import 'moment/locale/es';

export const getTiempo = () => moment().get('hour');

const formatDate = (date, format) => {
    const localLocale = moment(date);
    return localLocale.locale('es').format(format);
};

const formatTime = (date) => {
    const localLocale = moment(date);
    return localLocale.locale('es').format('HH:mm');
};

const renderMensajeBienvenida = () => {
    const tiempo = getTiempo();
    let mensaje = '';
    switch (true) {
        case tiempo >= 5 && tiempo < 12:
            mensaje = 'Buenos días';
            break;
        case tiempo >= 12 && tiempo < 20:
            mensaje = 'Buenas tardes';
            break;
        case tiempo >= 20 || tiempo < 5:
            mensaje = 'Buenas noches';
            break;
        default:
            mensaje = 'Buenos días';
    }
    return mensaje;
};

const formatFromNow = fecha => moment(fecha).fromNow();

export default {
    formatDate,
    formatTime,
    renderMensajeBienvenida,
    formatFromNow,
};
