/**
 * Funcion para obtener el porcentaje de progreso del component <ProgressBar></ProgressBar>
 * @param  {number} total total de pasos
 * @param  {number} actual paso actual
 */
const getProgreso = (total = 10, actual = 1) => {
    if (actual === 1) return 0;
    const porcentaje = (((actual * 100) / total) / 100);
    return porcentaje;
}

export default getProgreso;
