import React from 'react';
import { render } from '@testing-library/react-native';
import { CardItemSimulacionCredito, HelperStyle } from '../../../src/index';

describe('Test componente <CardItemSimulacionCredito />', () => {
    const data = {
        title: 'title',
        iconName: 'icon-bch-home',
        iconLabel: 'icon-bch-home',
        topTitle: 'topTitle',
        topValue: 'topValue',
        middleLeftTitle: 'middleLeftTitle',
        middleLeftValue: 'middleLeftValue',
        middleRightTitle: 'middleRightTitle',
        middleRightValue: 'middleRightValue',
        button: { title: 'boton' },
        list: [
            {
                label: 'label',
                value: 'value',
            },
            {
                label: 'label',
                value: 'value',
            },
            {
                label: 'label',
                value: 'value',
            },
        ],
    };
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<CardItemSimulacionCredito data={data} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar simulaciín activa', () => {
        const { getByText } = render(
            <CardItemSimulacionCredito data={data} active />,
        );
        const elemTitle = getByText(data.title);
        expect(Object.assign(...elemTitle.props.style.flat())).toEqual(
            expect.objectContaining(HelperStyle.white),
        );
    });
});
