import React from 'react';
import { render } from '@testing-library/react-native';
import { KeyboardAvoidingView, Input } from '../../../src';

describe('Test componente <KeyboardAvoidingView />', () => {
    const renderComponent = () => <Input label="Input" />;
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(
            <KeyboardAvoidingView>{renderComponent()}</KeyboardAvoidingView>,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería deshabilitar correción a hasDefaultKeyboardVerticalOffset', () => {
        const { toJSON } = render(
            <KeyboardAvoidingView hasDefaultKeyboardVerticalOffset={false}>
                {renderComponent()}
            </KeyboardAvoidingView>,
        );
        expect(toJSON()).toMatchSnapshot();
    });
});
