import React from 'react';
import { render } from '@testing-library/react-native';
import AvatarList from '../../../src/components/avatarList/avatarList';

describe('Test componente <AvatarList />', () => {
    const props = {
        data: [
            {
                label: 'l',
                monto: 1,
                small: true,
                closeIcon: true,
            },
        ],
        dataCustom: [
            {
                nombre: 'laura',
                apellido: 'vargas',
                small: false,
                closeIcon: false,
                onPress: () => 'llamada funcion',
            },
        ],
        testID: 'test_avatarlist',
    };

    test('Debería renderizar correctamente', () => {
        const { data } = props;
        const { toJSON } = render(<AvatarList data={data} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar Avatars con prop name', () => {
        const { dataCustom, testID } = props;
        const { getByTestId } = render(
            <AvatarList data={dataCustom} testID={testID} />,
        );
        const avatarList = getByTestId(testID);
        expect(avatarList).toBeDefined();
    });
});
