import React from 'react';
import { render } from '@testing-library/react-native';
import BouncingBall from '../../../src/components/bounceIndicator/BouncingBall';
import { HelperStyle } from '../../../src';

describe('Test componente <BouncingBall />', () => {
    const props = {
        testID: 'test_BouncingBall',
    };
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<BouncingBall delay={500} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar con color personalizado', () => {
        const { testID } = props;
        const { getByTestId } = render(
            <BouncingBall
                testID={testID}
                delay={500}
                color={HelperStyle.brand.color}
            />,
        );
        const bouncingBallElement = getByTestId(testID);
        expect(bouncingBallElement).toBeDefined();
    });
});
