import React from 'react';
import { render, fireEvent } from '@testing-library/react-native';
import { BoxPressable, Text } from '../../../src/index';

describe('Test componente <BoxPressable />', () => {
    const mockOnPress = jest.fn();
    test('DeberÃ­a renderizar correctamente', () => {
        const { toJSON, getByTestId } = render(
            <BoxPressable testID="BoxPressable" onPress={mockOnPress}>
                <Text small>Esse reprehenderit</Text>
            </BoxPressable>,
        );
        const elemBox = getByTestId('BoxPressable');
        fireEvent.press(elemBox);
        expect(mockOnPress).toHaveBeenCalledTimes(1);
        expect(toJSON()).toMatchSnapshot();
    });
});
