import React from 'react';
import { render } from '@testing-library/react-native';
import { CardResumen } from '../../../src/index';

describe('Test componente <CardResumen />', () => {
    const data = [
        { label: 'label', value: 'value' },
        { label: 'label', value: 'value' },
        { label: 'label', value: 100 },
    ];
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<CardResumen data={data} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar data tipo objecto', () => {
        const { toJSON } = render(<CardResumen data={data[0]} />);
        expect(toJSON()).toMatchSnapshot();
    });
});
