import React from 'react';
import { render } from '@testing-library/react-native';
import CheckBoxGroup from '../../../src/components/checkBoxGroup/CheckBoxGroup';
import { HelperStyle } from '../../../src';

describe('Test componente <CheckBoxGroup />', () => {
    const props = {
        testID: 'test_checkBoxGroup',
        items: [
            {
                checked: true,
                style: HelperStyle.brand,
            },
            {
                checked: false,
            },
        ],
    };
    test('Debería renderizar correctamente', () => {
        const { items } = props;
        const { toJSON } = render(<CheckBoxGroup items={items} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar checkboxs en horizontal', () => {
        const { items, testID } = props;
        const { getByTestId } = render(
            <CheckBoxGroup testID={testID} items={items} horizontal />,
        );
        const checkBoxElement = getByTestId(testID);
        expect(checkBoxElement).toBeDefined();
    });

    test('Debería renderizar con estilo en el container de los checkbox', () => {
        const { items, testID } = props;
        const { getByTestId } = render(
            <CheckBoxGroup
                testID={testID}
                items={items}
                style={HelperStyle.mb_4}
            />,
        );
        const checkBoxElement = getByTestId(testID);
        expect(checkBoxElement).toBeDefined();
    });
});
