import React from 'react';
import { render } from '@testing-library/react-native';
import { CountDownTimer } from '../../../src/index';

describe('Test componente <CountDownTimer />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<CountDownTimer totalDuration={1000} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería iniciar contador', () => {
        const { toJSON } = render(
            <CountDownTimer start totalDuration={1000} />,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería iniciar contador con animacion cambio de color', () => {
        const { toJSON } = render(
            <CountDownTimer start totalDuration={1000} animatedColor />,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería iniciar contador sin formato', () => {
        const { toJSON } = render(
            <CountDownTimer start totalDuration={1000} isFormatted={false} />,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería iniciar contador sin formato en milisegundos', () => {
        const { toJSON } = render(
            <CountDownTimer
                start
                msecs
                totalDuration={1000}
                isFormatted={false}
            />,
        );
        expect(toJSON()).toMatchSnapshot();
    });
});
