import React from 'react';
import { render } from '@testing-library/react-native';
import { Footer, Text } from '../../../src/index';

describe('Test componente <Footer />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(
            <Footer>
                <Text>footer</Text>
            </Footer>,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar footer colapsable', () => {
        const { toJSON, rerender } = render(
            <Footer collapsible collapsed>
                <Text>footer</Text>
            </Footer>,
        );
        rerender(
            <Footer collapsible collapsed={false}>
                <Text>footer</Text>
            </Footer>,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar footer tabBar', () => {
        const { toJSON } = render(
            <Footer tabBar>
                <Text>footer</Text>
            </Footer>,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar footer tabBar collapsible', () => {
        const { toJSON } = render(
            <Footer collapsible collapsed tabBar>
                <Text>footer</Text>
            </Footer>,
        );
        expect(toJSON()).toMatchSnapshot();
    });
});
