import React from 'react';
import { render } from '@testing-library/react-native';
import H4 from '../../../src/components/h4/h4';
import Style from '../../../src/components/h4/style';
import { HelperStyle } from '../../../src';

describe('Test componente <H4 />', () => {
    const props = {
        children: 'Texto ejemplo',
        style: HelperStyle.primary,
        headerTitle: true,
    };

    test('Debería renderizar correctamente', () => {
        const { style, children } = props;
        const { toJSON } = render(<H4 style={style}>{children}</H4>);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar texto', () => {
        const { children } = props;
        const { queryByText } = render(<H4>{children}</H4>);
        expect(queryByText(props.children)).not.toBeNull();
    });

    test('Debería tener estilo headerTitle', () => {
        const { children, headerTitle } = props;
        const { queryByText } = render(
            <H4 headerTitle={headerTitle}>{children}</H4>,
        );
        const element = queryByText(props.children);
        expect(element.props.style).toEqual(
            expect.arrayContaining([
                expect.objectContaining(Style.headerTitle),
            ]),
        );
    });
});
