import React from 'react';
import { render } from '@testing-library/react-native';
import H5 from '../../../src/components/h5/h5';
import { HelperStyle } from '../../../src';

describe('Test componente <H5 />', () => {
    const props = {
        children: 'Texto ejemplo',
        style: HelperStyle.primary,
    };

    test('Debería renderizar correctamente', () => {
        const { style, children } = props;
        const { toJSON } = render(<H5 style={style}>{children}</H5>);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar texto', () => {
        const { children } = props;
        const { queryByText } = render(<H5>{children}</H5>);
        expect(queryByText(props.children)).not.toBeNull();
    });
});
