import React from 'react';
import { render } from '@testing-library/react-native';
import { HeaderTitleLogo, HelperStyle } from '../../../src/index';
import HEADERTITLELOGO_STR from '../../../src/components/headerTitleLogo/headerTitleLogoStr';

describe('Test componente <HeaderTitleLogo />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<HeaderTitleLogo />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar variantes white', () => {
        const { getByTestId } = render(<HeaderTitleLogo white />);
        const iconLogoBanco = getByTestId(HEADERTITLELOGO_STR.brand.BCH);
        expect(iconLogoBanco).toBeDefined();
        expect(Object.assign(...iconLogoBanco.props.style.flat(2))).toEqual(
            expect.objectContaining(HelperStyle.white),
        );
    });
});
