import React from 'react';
import { render } from '@testing-library/react-native';
import Highlighter from '../../../src/components/highlighter/Highlighter';
import Style from '../../../src/components/highlighter/style';

describe('Test componente <Highlighter />', () => {
    const props = {
        searchWords: ['Hola', 'como'],
        textToHighlight: 'Hola, como estas? soy Highlighter component',
        testID: 'Highlighter',
    };
    const { searchWords, textToHighlight, testID } = props;
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(
            <Highlighter
                searchWords={searchWords}
                textToHighlight={textToHighlight}
            />,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería tener estilo para numeros', () => {
        const { getByTestId } = render(
            <Highlighter
                searchWords={searchWords}
                textToHighlight={textToHighlight}
                testID={testID}
                number
            />,
        );
        const element = getByTestId(testID);
        expect(element.props.style).toEqual(
            expect.arrayContaining([expect.objectContaining(Style.number)]),
        );
    });
});
