import React from 'react';
import { render } from '@testing-library/react-native';
import Icon from '../../../src/components/icon/Icon';

describe('Test componente <Icon />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<Icon name="icon-bch-home" />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar icono de otra familia tipografica', () => {
        const { toJSON } = render(<Icon name="mic-off" type="Ionicons" />);
        expect(toJSON()).toMatchSnapshot();
    });
});
