import React from 'react';
import { render } from '@testing-library/react-native';
import InputHelperText from '../../../src/components/inputHelperText/inputHelperText';
import { HelperStyle } from '../../../src';

describe('Test componente <InputHelperText />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(
            <InputHelperText error style={HelperStyle.mb_4}>
                error
            </InputHelperText>,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar mensaje con animación', () => {
        const { getByText, rerender } = render(<InputHelperText />);
        rerender(
            <InputHelperText style={HelperStyle.mb_4}>ayuda</InputHelperText>,
        );
        expect(getByText('ayuda')).toBeDefined();
    });
});
