import React from 'react';
import { render } from '@testing-library/react-native';
import { LogoIcon, HelperStyle, HelperOptions } from '../../../src';
import LOGOICON_STR from '../../../src/components/logoIcon/LogoIconStr';

describe('Test componente <LogoIcon />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<LogoIcon style={HelperStyle.brand} />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar marca bec', () => {
        HelperOptions.brand = 2;
        const { getByTestId } = render(<LogoIcon style={HelperStyle.brand} />);
        expect(getByTestId(LOGOICON_STR.brandLabelBec)).toBeDefined();
    });
    test('Debería renderizar marca cch', () => {
        HelperOptions.brand = 3;
        const { getByTestId } = render(<LogoIcon style={HelperStyle.brand} />);
        expect(getByTestId(LOGOICON_STR.brandLabelCch)).toBeDefined();
    });
});
