import React from 'react';
import Color from 'color';
import { render } from '@testing-library/react-native';
import { Pagination, HelperStyle } from '../../../src/index';

describe('Test componente <Pagination />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(
            <Pagination dotsLength={5} activeDotIndex={1} />,
        );
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería mostrar pagination light theme', () => {
        const { toJSON, getByTestId } = render(
            <Pagination
                dotsLength={5}
                activeDotIndex={0}
                paginationStyle="light"
            />,
        );
        const elemDot = getByTestId('Página 1');
        expect(
            Color(elemDot.props.animatedStyle.value.backgroundColor).hex(),
        ).toBe(HelperStyle.white.color);
        expect(toJSON()).toMatchSnapshot();
    });
});
