import React from 'react';
import { Platform } from 'react-native';
import { render } from '@testing-library/react-native';
import { Skeleton } from '../../../src/components/index';

describe('Test componente <Skeleton />', () => {
    test('Debería renderizar correctamente Skeleton ImageContent', () => {
        const { toJSON } = render(<Skeleton type="ImageContent" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton imageline', () => {
        const { toJSON } = render(<Skeleton type="imageline" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton imageline small', () => {
        const { toJSON } = render(<Skeleton type="imageline" small />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton Paragraph', () => {
        const { toJSON } = render(<Skeleton type="Paragraph" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton Box', () => {
        const { toJSON } = render(<Skeleton type="Box" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton Media', () => {
        const { toJSON } = render(<Skeleton type="Media" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton input', () => {
        const { toJSON } = render(<Skeleton type="input" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton Button', () => {
        const { toJSON } = render(<Skeleton small type="Button" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton line', () => {
        const { toJSON } = render(<Skeleton type="line" numberOfItems={4} />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton Alert', () => {
        const { toJSON } = render(
            <Skeleton col type="Alert" numberOfItems={4} />,
        );
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton Paragraph', () => {
        const { toJSON } = render(
            <Skeleton transparent small type="Paragraph" />,
        );
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton radio', () => {
        const { toJSON } = render(<Skeleton type="radio" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton switch Android', () => {
        Platform.OS = 'android';
        const { toJSON } = render(<Skeleton type="switch" />);
        expect(toJSON()).toMatchSnapshot();
    });
    test('Debería renderizar correctamente Skeleton switch IOS', () => {
        const { toJSON } = render(<Skeleton type="switch" />);
        expect(toJSON()).toMatchSnapshot();
    });
});
