import React from 'react';
import { render } from '@testing-library/react-native';
import { HelperStyle, Text } from '../../../src';
import SwitchLabel from '../../../src/components/switch/SwitchLabel';

const label = 'Do consectetur amet officia elit amet.';
const renderLabel = () => <Text style={HelperStyle.success}>{label}</Text>;

describe('Test componente <SwitchLabel />', () => {
    test('Debería renderizar correctamente', () => {
        const { toJSON } = render(<SwitchLabel label={label} />);
        expect(toJSON()).toMatchSnapshot();
    });

    test('Debería renderizar con label personalizado color success', () => {
        const { getByText } = render(<SwitchLabel renderLabel={renderLabel} />);
        const labelElement = getByText(label);
        expect(labelElement).toBeDefined();
        expect(Object.assign(...labelElement.props.style.flat())).toEqual(
            expect.objectContaining(HelperStyle.success),
        );
    });
});
