import { Spacing, Base, smallBase, Scale } from '../../src/constants/spacing';

jest.mock('react-native/Libraries/Utilities/Platform', () => ({
    OS: 'ios',
    select: () => null,
}));
jest.mock('react-native/Libraries/Utilities/Dimensions', () => ({
    get: jest.fn().mockReturnValue({
        width: 320,
        height: 568,
    }),
}));

describe('Test constantes Spacing', () => {
    test('Debería construir los espaciados para dispositivos pequeños', () => {
        expect(Spacing).toBeDefined();
        expect(Base).toBe(smallBase);
    });

    test('Debería escalar el value a la escala para dispositivos pequeños', () => {
        const value = Scale(smallBase);
        expect(value).not.toBe(smallBase);
    });
});
