import ItemStyle from '../../../native-base/components/Item';
import Minerva from '../../../native-base/variables/minerva';
import Platform from '../../../src/constants/platform';

jest.mock('../../../src/constants/platform', () => ({
    android: false,
    ios: true,
}));

describe('Test NativeBase ItemStyle', () => {
    test('Debería cargar estilos para android', () => {
        const item = ItemStyle(Minerva);
        Platform.android = true;
        Platform.ios = false;
        expect(item).toBeDefined();
    });

    test('Debería estar definido', () => {
        const item = ItemStyle(Minerva);
        expect(item).toBeDefined();
    });
});
