import React from 'react';
import { render } from '@testing-library/react-native';
import { Text, HelperStyle, ColorsBCH } from '../../src/index';
import Theme from '../../src/util/Theme';

describe('Test util getStatusBarHeight', () => {
    test('DeberÃ­a mostrar tema bch', () => {
        const text = 'Velit ipsum id minim';
        const { getByText } = render(
            <Theme>
                <Text small style={HelperStyle.primary}>
                    {text}
                </Text>
            </Theme>,
        );
        const elemText = getByText(text);
        expect(Object.assign(...elemText.props.style.flat()).color).toBe(
            ColorsBCH.primary,
        );
    });
});
