import { Platform } from 'react-native';
import getStatusBarHeight from '../../src/util/getStatusBarHeight';

jest.mock('react-native/Libraries/Utilities/Dimensions', () => ({
    ...jest.requireActual('react-native/Libraries/Utilities/Dimensions'),
    get: jest.fn().mockReturnValue({ width: 375, height: 812 }),
}));

describe('Test util getStatusBarHeight', () => {
    test('Deberia obtener getStatusBarHeight ios', () => {
        expect(getStatusBarHeight()).toBe(44);
    });

    test('Deberia obtener getStatusBarHeight android', () => {
        Platform.OS = 'android';
        Platform.select = () => 0;
        const skipAndroid = true;
        expect(getStatusBarHeight(skipAndroid)).toBe(0);
    });
});
