// @flow

import { Platform } from 'react-native';
import { Scale } from '../../src/index';
import variable from '../variables/platform';
import { PLATFORM } from '../variables/commonColor';

export default (variables /* : * */ = variable) => ({
    '.selected': {
        'NativeBase.IconNB': {
            color:
                Platform.OS === PLATFORM.IOS
                    ? variables.radioColor
                    : variables.radioSelectedColorAndroid,
            lineHeight:
                Platform.OS === PLATFORM.IOS
                    ? Scale(25)
                    : variables.radioBtnLineHeight,
            height: Platform.OS === PLATFORM.IOS ? Scale(20) : undefined,
        },
    },
    'NativeBase.IconNB': {
        color: Platform.OS === PLATFORM.IOS ? 'transparent' : undefined,
        lineHeight:
            Platform.OS === PLATFORM.IOS
                ? undefined
                : variables.radioBtnLineHeight,
        fontSize:
            Platform.OS === PLATFORM.IOS ? undefined : variables.radioBtnSize,
    },
});
