import { Spacing, Fonts, minerva } from '../../src/index';

export default (variables /* : * */ = minerva) => ({
    fontSize: Spacing.type_3,
    fontFamily: variables.fontFamily,
    color: variables.textColor,
    lineHeight: Spacing.lineHeight_2,
    marginBottom: Spacing.spacer_4,
    '.disabled': {
        color: variables.gray_lighter,
    },
    '.bold': {
        fontFamily: Fonts.fontFamilyBold,
    },
    '.uppercase': {
        textTransform: 'uppercase',
    },
    '.small': {
        fontSize: Spacing.type_2,
    },
    '.smaller': {
        fontSize: Spacing.type_1,
    },
    '.number': {
        fontFamily: Fonts.fontFamilyBold,
        letterSpacing: 1,
    },
    '.unformatted': {
        lineHeight: Spacing.lineHeight_1,
        marginBottom: 0,
    },
});
