#!/bin/bash

upgradeTodo(){
    rm -rf android/app/src/main/assets/animations/; cp -R node_modules/bch-assets/animations android/app/src/main/assets/animations;rm -rf android/app/src/main/assets/fonts/; cp -R node_modules/bch-assets/fonts android/app/src/main/assets/fonts;rm -rf android/app/src/main/assets/fonts/selection.json;
    echo "Lottie y Fonts actualizados"
    break
}

upgradeLottie(){
    rm -rf android/app/src/main/assets/animations/; cp -R node_modules/bch-assets/animations android/app/src/main/assets/animations
    echo "Lottie actualizados"
    break
}

upgradeFonts(){
    rm -rf android/app/src/main/assets/fonts/; cp -R node_modules/bch-assets/fonts android/app/src/main/assets/fonts;rm -rf android/app/src/main/assets/fonts/selection.json;
    echo "Fonts actualizados"
    break
}


PS3='Seleccione los assets que desea agregar o actualizar en la carpeta android: '
options=("Fonts y lottie" "Fonts" "Lottie"  "salir...")
select opt in "${options[@]}"
do
    case $opt in
        "Fonts y lottie")
            upgradeTodo;;
        "Fonts")
            upgradeFonts;;
        "Lottie")
            upgradeLottie;;
        "salir...")
            break 
            ;;
        *) echo "Opci√≥n $REPLY no existe";;
    esac
done