#! /bin/sh

# Colores
R='\033[0;31m'
G='\033[0;32m'
NC='\033[0m' # No Color

if [ -d "./node_modules" ];
then
    read -p "¿Cuál es el nombre del componente? " yn
    node scripts/generate/command-line crear-componente __nombreComponente__="$yn"
else
    echo "Requiere instalar dependencias...";
    if yarn install --no-lockfile ; then
        echo "${G}Dependencias instaladas${NC}"
        read -p "¿Cuál es el nombre del componente? " yn
        node scripts/generate/command-line crear-componente __nombreComponente__="$yn"
    else
        echo "${R}[ERROR]${NC} Para la generación de plantilla es necesario tener instalado yarn https://classic.yarnpkg.com/en/docs/install/#mac-stable"
    fi
    
fi
