import React from 'react';
import { View } from 'react-native';
import { ButtonsGroup, HelperStyle, Body } from '../../../index';
import { propTypes, defaultProps } from '../type';

const AlertBody = ({ buttonsGroup, children }) => {
    if (buttonsGroup) {
        return (
            <Body>
                <View
                    style={[
                        HelperStyle.flx_row,
                        HelperStyle.flx_wrap,
                        HelperStyle.ai_c,
                        HelperStyle.jc_fe,
                    ]}
                >
                    <View style={[HelperStyle.flx_grow, HelperStyle.jc_c]}>
                        {children}
                    </View>
                    <View style={[HelperStyle.pt_2, HelperStyle.pb_2]}>
                        <ButtonsGroup
                            horizontal
                            small
                            neutral
                            styleItems={HelperStyle.flx_0}
                            items={buttonsGroup}
                        />
                    </View>
                </View>
            </Body>
        );
    }
    return <Body>{children}</Body>;
};

AlertBody.propTypes = {
    buttonsGroup: propTypes.buttonsGroup,
    children: propTypes.children.isRequired,
};
AlertBody.defaultProps = {
    buttonsGroup: defaultProps.buttonsGroup,
};

export default AlertBody;
