import React, { memo } from 'react';
import { HelperStyle, View } from '../../index';
import BouncingBall from './BouncingBall';
import { propTypes, defaultProps } from './type';

/**
 * Componente para mostrar animaciÃ³n para estados loading.
 *
 * @component
 */
const BounceIndicator = ({ color, testID, size }) => (
    <View style={HelperStyle.flx_row} testID={testID}>
        <BouncingBall delay={0} color={color} size={size} />
        <BouncingBall delay={500} color={color} size={size} />
        <BouncingBall delay={700} color={color} size={size} />
    </View>
);

BounceIndicator.propTypes = {
    color: propTypes.color,
    testID: propTypes.testID,
    size: propTypes.size,
};
BounceIndicator.defaultProps = {
    color: defaultProps.color,
    testID: defaultProps.testID,
    size: defaultProps.size,
};

export default memo(BounceIndicator);
