import { StyleSheet } from 'react-native';
import { Scale } from '../../index';

const Style = (props) => {
    const { color, size } = props;
    return StyleSheet.create({
        size: {
            width: size,
            height: size,
            borderRadius: size / 2,
            marginHorizontal: Scale(2),
        },
        bgcolor: {
            backgroundColor: color,
        },
    });
};

export default Style;
