import React from 'react';
import PropTypes from 'prop-types';
import { View, HelperStyle } from '../../index';
import { DEFAULT_TYPES, TYPES } from '../../constants/propTypes';

/**
 * Componente para mostrar una caja.
 *
 * @component
 */
const Box = ({
    children,
    style,
    testID,
    accessibilityLabel,
    accessibilityHint,
}) => (
    <View
        testID={testID}
        accessibilityLabel={accessibilityLabel}
        accessibilityHint={accessibilityHint}
        style={[
            HelperStyle.bg_white,
            HelperStyle.shadow,
            HelperStyle.br_3,
            style,
        ]}
    >
        {children}
    </View>
);

Box.propTypes = {
    /**
     * Estilos.
     */
    style: TYPES.style,
    /**
     * Componente a renderizar dentro del box.
     */
    children: PropTypes.oneOfType([PropTypes.node, PropTypes.func]).isRequired,
    /**
     * Identificador para pruebas.
     */
    testID: PropTypes.string,
    /**
     * Identificador para accesibilidad.
     */
    accessibilityLabel: PropTypes.string,
    /**
     * Sugerencia de accesibilidad.
     */
    accessibilityHint: PropTypes.string,
};

Box.defaultProps = {
    style: DEFAULT_TYPES.style,
    testID: '',
    accessibilityLabel: '',
    accessibilityHint: '',
};

export default Box;
