import React from 'react';
import { HelperStyle, PressableOpacity } from '../../index';
import { propTypes, defaultProps } from '../pressableOpacity/type';
import buildStyle from './style';

/**
 * Componente para mostrar una caja accionable.
 *
 * @component
 */
const BoxPressable = ({
    children,
    delayLongPress,
    disabled,
    hitSlop,
    onLongPress,
    onPress,
    onPressIn,
    onPressOut,
    pressRetentionOffset,
    style,
    testID,
    accessibilityLabel,
    accessibilityHint,
}) => {
    const Style = buildStyle({ HelperStyle, style });
    return (
        <PressableOpacity
            delayLongPress={delayLongPress}
            disabled={disabled}
            hitSlop={hitSlop}
            onLongPress={onLongPress}
            onPress={onPress}
            onPressIn={onPressIn}
            onPressOut={onPressOut}
            pressRetentionOffset={pressRetentionOffset}
            testID={testID}
            accessibilityLabel={accessibilityLabel}
            accessibilityHint={accessibilityHint}
            style={Style}
        >
            {children}
        </PressableOpacity>
    );
};

BoxPressable.propTypes = propTypes;

BoxPressable.defaultProps = defaultProps;

export default BoxPressable;
