import { StyleSheet } from 'react-native';

const Style = ({ HelperStyle, horizontal }) => {
    const horizontalContainerStyle = {
        ...HelperStyle.flx_i,
        ...HelperStyle.mv_1,
        ...HelperStyle.ml_2,
    };
    return StyleSheet.create({
        box: {
            ...(horizontal ? HelperStyle.flx_row : HelperStyle.flx_i),
            ...(horizontal ? HelperStyle.pa_2 : HelperStyle.pa_4),
        },
        imageContainerStyle: {
            ...(horizontal ? null : HelperStyle.flx_i),
            ...HelperStyle.minWidth(133),
        },
        contentContainerStyle: {
            ...(horizontal ? horizontalContainerStyle : HelperStyle.mt_2),
        },
    });
};

export default Style;
