import React, { memo } from 'react';
import {
    CardBodyItemTitlePropTypes,
    CardBodyItemTitleDefaultProps,
} from '../../type';
import { Text } from '../../../../index';

/**
 * Componente titulo del item del detalle del card body (list).
 *
 * @component
 */
const CardBodyItemTitle = ({ title, styleTitle }) => (
    <Text
        small
        numberOfLines={1}
        ellipsizeMode="tail"
        accessibilityLabel={title}
        testID={title}
        style={styleTitle}
    >
        {title}
    </Text>
);

CardBodyItemTitle.propTypes = CardBodyItemTitlePropTypes;
CardBodyItemTitle.defaultProps = CardBodyItemTitleDefaultProps;

export default memo(CardBodyItemTitle);
