import React, { memo } from 'react';
import {
    CardBodyItemValuePropTypes,
    CardBodyItemValueDefaultProps,
} from '../../type';
import { H3, Text, HelperStyle } from '../../../../index';

/**
 * Componente valor del item del detalle del card body (list).
 *
 * @component
 */
const CardBodyItemValue = ({
    value,
    index,
    valueDestacado,
    styleValue,
    valueIndicadorShow,
}) => {
    const styleValueIndicadorShow = valueIndicadorShow
        ? HelperStyle.mb_0
        : null;
    if (index === 0 || valueDestacado) {
        return (
            <H3
                number
                accessibilityLabel={value}
                testID={value}
                style={[styleValueIndicadorShow, styleValue]}
            >
                {value}
            </H3>
        );
    }
    return (
        <Text
            number
            style={[
                HelperStyle.gray_dark,
                HelperStyle.as_c,
                styleValueIndicadorShow,
                styleValue,
            ]}
            accessibilityLabel={value}
            testID={value}
        >
            {value}
        </Text>
    );
};

CardBodyItemValue.propTypes = CardBodyItemValuePropTypes;
CardBodyItemValue.defaultProps = CardBodyItemValueDefaultProps;

export default memo(CardBodyItemValue);
