import React, { memo } from 'react';
import {
    CardBodyRightLeftPropTypes,
    CardBodyRightLeftdefaultProps,
} from '../../type';
import { Skeleton, HelperStyle } from '../../../../index';
import CardBodyItemTitle from './CardBodyItemTitle';
import CardBodyItemValue from './CardBodyItemValue';

/**
 * Componente area del contenido izquierdo del card de Productos.
 *
 * @component
 */
const CardBodyLeft = ({ item, index, isArrayOfTwoItems }) => {
    const { value, title, valueLoading, styleValue, styleTitle, renderLeft } =
        item;
    if (!title && renderLeft) {
        return renderLeft;
    }
    if (isArrayOfTwoItems(title)) {
        return <CardBodyItemTitle title={title[0]} styleTitle={styleTitle} />;
    }
    if (isArrayOfTwoItems(value)) {
        if (valueLoading) {
            return <Skeleton numberOfItems={1} style={HelperStyle.mb_0} />;
        }
        return (
            <CardBodyItemValue
                value={value[0]}
                index={index}
                styleValue={styleValue}
            />
        );
    }
    return <CardBodyItemTitle title={title} styleTitle={styleTitle} />;
};

CardBodyLeft.propTypes = CardBodyRightLeftPropTypes;
CardBodyLeft.defaultProps = CardBodyRightLeftdefaultProps;

export default memo(CardBodyLeft);
