import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { HelperStyle, Icon } from '../../../../index';
import CARDITEMPRODUCTO_STR from '../../CardItemProductoStr';

/**
 * Componente icono que se muestra al lado del value en "list" para la rentabilidad positiva y negativa.
 * True rentabilidad positiva: será un icono de flecha UP, color verde.
 * False rentabilidad negativa: será un icono de flecha DOWN, color rojo.
 *
 * @component
 */
const CardIndicatorIconArrow = ({ valueIndicadorState }) => {
    const iconUp = {
        colorIcono: HelperStyle.success,
        nombreIcono: 'icon-bch-arrow-order-up',
        testIDIcono: CARDITEMPRODUCTO_STR.iconoRentabilidadPositivo,
    };
    const iconDown = {
        colorIcono: HelperStyle.danger,
        nombreIcono: 'icon-bch-arrow-order-down',
        testIDIcono: CARDITEMPRODUCTO_STR.iconoRentabilidadNegativo,
    };
    const configIcon = valueIndicadorState === true ? iconUp : iconDown;
    const { colorIcono, nombreIcono, testIDIcono } = configIcon;
    return (
        <Icon
            style={[colorIcono, HelperStyle.fs_4]}
            name={nombreIcono}
            accessible
            accessibilityLabel={testIDIcono}
            testID={testIDIcono}
        />
    );
};

CardIndicatorIconArrow.propTypes = {
    valueIndicadorState: PropTypes.bool,
};
CardIndicatorIconArrow.defaultProps = {
    valueIndicadorState: false,
};

export default memo(CardIndicatorIconArrow);
