import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { PRODUCTS } from 'bch-mobile-constants';
import { Image } from 'react-native';
import Images from 'bch-assets';
import { HelperStyle, View, Scale } from '../../../../index';

/**
 * Componente imagen para los productos mi inversion que se muestran en el lado derecho del encabezado del producto.
 *
 * @component
 */
const IconosMiInversion = ({ tipoProducto }) => {
    const imgStyle = { width: Scale(30), height: Scale(30) };
    const {
        miInversion: {
            productos: {
                accionesChile,
                accionesMundo,
                apv,
                etf,
                fondosDeInversion,
                compraDolares,
                dap,
                fondosMutuos,
                otrosProductos,
                caja,
                cuentaAhorro,
                pershing,
            },
        },
    } = Images;
    const {
        PRODUCT_TYPE: {
            PRODUCT_CA_MI_INVERSION,
            PRODUCT_DAP,
            PRODUCT_BILLETERA,
            PRODUCT_FFMM,
            PRODUCT_ACCIONES_NACIONALES,
            PRODUCT_ACCIONES_INTERNACIONALES,
            PRODUCT_ETF,
            PRODUCT_COMPRA_DOLARES,
            PRODUCT_COMPRA_VENTA_DOLARES,
            PRODUCT_FONDOS_INVERSION,
            PRODUCT_APV,
            PRODUCT_OTROS,
            PRODUCT_PERSHING,
        },
        NAME_PRODUCT: {
            CUENTA_AHORRO_ICONO,
            DAP,
            CAJA,
            FFMM,
            ACCIONES_NACIONALES,
            ACCIONES_INTERNACIONALES,
            ETF,
            COMPRA_DOLARES,
            COMPRA_VENTA_DOLARES,
            FONDOS_INVERSION,
            APV,
            OTROS,
            PERSHING,
        },
    } = PRODUCTS;

    const configImage = {};
    configImage[PRODUCT_DAP] = {
        source: dap,
        accessibilityLabel: DAP,
    };
    configImage[PRODUCT_CA_MI_INVERSION] = {
        source: cuentaAhorro,
        accessibilityLabel: CUENTA_AHORRO_ICONO,
    };
    configImage[PRODUCT_BILLETERA] = {
        source: caja,
        accessibilityLabel: CAJA,
    };
    configImage[PRODUCT_FFMM] = {
        source: fondosMutuos,
        accessibilityLabel: FFMM,
    };
    configImage[PRODUCT_ACCIONES_NACIONALES] = {
        source: accionesChile,
        accessibilityLabel: ACCIONES_NACIONALES,
    };
    configImage[PRODUCT_ACCIONES_INTERNACIONALES] = {
        source: accionesMundo,
        accessibilityLabel: ACCIONES_INTERNACIONALES,
    };
    configImage[PRODUCT_ETF] = {
        source: etf,
        accessibilityLabel: ETF,
    };
    configImage[PRODUCT_COMPRA_DOLARES] = {
        source: compraDolares,
        accessibilityLabel: COMPRA_DOLARES,
    };
    configImage[PRODUCT_COMPRA_VENTA_DOLARES] = {
        source: compraDolares,
        accessibilityLabel: COMPRA_VENTA_DOLARES,
    };
    configImage[PRODUCT_FONDOS_INVERSION] = {
        source: fondosDeInversion,
        accessibilityLabel: FONDOS_INVERSION,
    };
    configImage[PRODUCT_APV] = {
        source: apv,
        accessibilityLabel: APV,
    };
    configImage[PRODUCT_OTROS] = {
        source: otrosProductos,
        accessibilityLabel: OTROS,
    };
    configImage[PRODUCT_PERSHING] = {
        source: pershing,
        accessibilityLabel: PERSHING,
    };
    const { source, accessibilityLabel } = configImage[tipoProducto];
    return (
        <View style={[HelperStyle.as_c, HelperStyle.jc_c]}>
            <Image
                source={source}
                style={imgStyle}
                accessible
                accessibilityLabel={accessibilityLabel}
                testID={accessibilityLabel}
                resizeMode="contain"
            />
        </View>
    );
};

IconosMiInversion.propTypes = {
    tipoProducto: PropTypes.string.isRequired,
};

export default memo(IconosMiInversion);
